import React, { Component } from 'react';
import PropTypes from 'prop-types';
import "./Recipe.css";
import axios from 'axios';



class Recipe extends Component {

  addRecipeActionCallback = () => {
    let id = this.props.recipe.uri
    id = encodeURIComponent(id);
    let url = `http://localhost:8080/addrecipe?id=${id}&useruid=${this.props.useruid}`

    axios.post(url)
    .then((response) => {
      console.log(response);
    })
    .catch((error) => {
      this.setState({
        error: error,
      });
    });

  };

  render () {
    console.log("in recipe");
    console.log(this.props);
    return(
      <div className="item-list_container">
        <ul className="recipe-items">
          <li>{this.props.recipe.label}</li>
          <li><img src={this.props.recipe.image} alt=''/></li>
          <li><button
            onClick={this.addRecipeActionCallback}
            className="item__button">Add to Calendar</button></li>
          <li><button  onClick={() => this.props.recipeDetailCallback()}>Recipe Details </button></li>
        </ul>
      </div>
    )
  }

}
Recipe.propTypes = {
  label: PropTypes.string

};


export default Recipe;
