import React, { Component } from 'react';
import axios from 'axios';
import Home from './Home';
import Recipes from './Recipes';
import Myrecipes from './Myrecipes';
import Details from './Details';

import { BrowserRouter as Router, Route, Link } from "react-router-dom";

import firebase, { auth, provider } from '../firebase.js';


class Dashboard extends Component {
  constructor() {
    super();
    this.login = this.login.bind(this); // <-- add this line
    this.logout = this.logout.bind(this); // <-- add this line

    this.state = {
      message: "",
      result: [],
      query: "",
      user: null,
      detailRecipe: "",
      searchbool: false,

    }
  }


  onFormSubmit = (event) => {
    event.preventDefault();
    this.setState({
      query: '',
      result: []
    });
    this.searchRecipes();
  }
  handleChange = (event) => {
    this.setState({query: event.target.value});
  }

  componentDidMount() {
    auth.onAuthStateChanged((user) => {
      if (user) {
        this.setState({ user });
        this.setState({searchbool: true});
        this.setState({message: `Welcome Back ${this.state.user.displayName}`})
      }
    });
  }

  login() {
    auth.signInWithPopup(provider)
    .then((result) => {
      const user = result.user;
      this.setState({
        user: user,
        searchbool: true

      });
    });
  }

  logout() {
    auth.signOut()
    .then(() => {
      this.setState({
        user: null,
        searchbool: false
      });
    });
  }

  searchRecipes = () => {
    const searchterm = this.state.query
    const url = `http://localhost:8080/search?ingredients=${this.state.query}`
    axios.get(url)
    .then((response) => {
      this.setState({
        result: response.data.hits,
        message: `Loading search for ${searchterm}`
      });
    })
    .catch((error) => {
      this.setState({
        error: error,
      });
    })

  };


  addRecipe = (recipe) => {
    console.log(recipe);
    console.log(this.state.user.uid);
    let id = recipe.uri
    id = encodeURIComponent(id);
    let url = `http://localhost:8080/addrecipe?id=${id}&useruid=${this.state.user.uid}`

    axios.post(url)
    .then((response) => {
      this.setState({
        message: `Successfully added ${recipe.label}`
      })
    })
    .catch((error) => {
      this.setState({
        error: error,
      });
    });

  };





  myrecipes = () => {

    const url = `http://localhost:8080/myrecipes?useruid=${this.state.user.uid}`
    axios.get(url)
    .then((response) => {
      this.setState({
        result: response.data,
        searchbool: true,

      })
    })
    .catch((error) => {
      this.setState({
        error
      });
    })
  }

  recipeDetail = (recipe) => {
    this.setState({
      detailRecipe: recipe,
      result: []
    })
    console.log(this.state.detailRecipe);
  }

  render() {
    return(
      <Router>
        <div>
          <div>
            <nav className="nav-list_container">
              <Link to="/"  className="dashboard-link">Home</Link>
              <form onSubmit={this.onFormSubmit}>
                <label>
                  Search Recipes:
                  <input type="text" value={this.state.query} onChange={this.handleChange} />
                </label>
                <input type="submit" value="Submit" />
              </form>
              <div className="wrapper">
                {this.state.user ?
                  <button onClick={this.logout}>Log Out</button>
                  :
                  <button onClick={this.login}>Log In</button>
                }
              </div>
              <div>
                {this.state.user ?
                  <button onClick={this.myrecipes}><Link to="/myaccount/" className="dashboard-link">My Account
                  </Link></button>
                  :
                  <p></p>
                }
              </div>

            </nav>

            <div>
              <p className={"status-bar__text"}>{this.state.message}</p>
            </div>

            <Route path="/" exact component={Home} />

            <Recipes
              recipeList={this.state.result}
              useruid={this.state.user ? this.state.user.uid : null}
              recipeDetailCallback={(recipe) => this.recipeDetail(recipe)}
              searchbool={this.state.searchbool}
              addRecipeActionCallback={(recipe) => this.addRecipe(recipe)}
              />

            <Route path="/myaccount/" render={() => <Myrecipes
                myrecipes={this.state.result}
                searchbool={this.state.searchbool}
                />} />

              <Details detailRecipe={this.state.detailRecipe}
                searchbool={this.state.searchbool}
                addRecipeActionCallback={(recipe) => this.addRecipe(recipe)}
                />

            </div>
          </div>
        </Router>
      )
    }


  }

  export default Dashboard;
