import React, { Component } from 'react';
import PropTypes from 'prop-types';
import { Card, CardImg, CardText, CardBody,
  CardTitle, CardSubtitle, Button } from 'reactstrap';
  class Details extends Component {


    render() {
      if (this.props.detailRecipe) {
        const ingredients = this.props.detailRecipe.ingredients.map((item, i) => {
          return (
            item.text
          )
        })


        const healthLabels = this.props.detailRecipe.healthLabels.map((item, i) => {
          return (
            item
          )
        })

        return (
          <div>


            <Card>
              <CardImg top width="100%" src={this.props.detailRecipe.image} />
              <CardBody>
                <CardTitle>{this.props.detailRecipe.label}</CardTitle>
                <CardSubtitle>Ingredients:</CardSubtitle>
                <CardText>{ingredients ? ingredients : "None"}</CardText>
                <CardSubtitle>HealthLabel:</CardSubtitle>
                <CardText>{healthLabels}</CardText>
                <Button>{((this.props.user && this.props.myaccountrecipedetail) || !(this.props.user)) ? null: <button onClick={() => this.props.addRecipeActionCallback(this.props.detailRecipe)}>Add Recipe</button> }</Button>

              </CardBody>
            </Card>




            <ul>
              <li>{this.props.detailRecipe.label}</li>
              <li><img src={this.props.detailRecipe.image} alt=''/></li>
              <li>UrL: {this.props.detailRecipe.url}</li>
              <li>Ingredients: {ingredients ? ingredients : "None"}</li>
              <li>healthLabels: {healthLabels}</li>
              <div>
                {((this.props.user && this.props.myaccountrecipedetail) || !(this.props.user)) ? null: <button onClick={() => this.props.addRecipeActionCallback(this.props.detailRecipe)}>Add Recipe</button> }
              </div>
            </ul>
          </div>
        )
      } else {
        return <div></div>;
        }
      }
    }

    export default Details;
